clear; clc;K = 8;x = load('s1.txt');n = length(x);mu = zeros(K, 2);chunkSize = round(n / K);% randomly initialize default centroids by picking existing pointfor k = 1:K  lowerBound = 1;  higherBound = n;  random = randi([lowerBound, higherBound]);  mu(k,1) = x(random, 1);  mu(k,2) = x(random, 2);end;cluster_indexes = zeros(1, n);for i = 1:10;      % assign each point to a cluster index  for j = 1:n;    x1 = x(j, 1);    y1 = x(j, 2);    clusterIndex = -1;    minimalDistance = Inf;        for k = 1:K;      x2 = mu(k, 1);      y2 = mu(k, 2);            % distance between cluster centroid point and each x,y      distance = sqrt((x1 - x2)^2 + (y1 - y2)^2);            if (distance < minimalDistance);        minimalDistance = distance;        clusterIndex = k;      end;    end;        cluster_indexes(j) = clusterIndex;  end;    % display centroids on the plot  scatter(x(:, 1), x(:, 2), 250, cluster_indexes, "filled");  hold on;  scatter(mu(:, 1), mu(:,2), 350, "r", "filled");  hold off;    pause(0.001);    % calculate new centroid point for each cluster  for k = 1:K;    m = 0;    sumX = 0;    sumY = 0;        for i = 1:n;      if (cluster_indexes(i) == k);        sumX += x(i, 1);        sumY += x(i, 2);        m++;      end;    end;        mu(k,1) = (sumX / m);    mu(k,2) = (sumY / m);  end;end;      