clear; clc;data = load('inputTrainingSet.txt');x = data(:, 1:2); % y = data(:, 3);m = length(y);[x mu sigma] = featureNormalize(x);x = [ones(m, 1) x];alpha = 0.01;num_iters = 1000;theta = zeros(3, 1);[theta, J_history] = gradient(x, y, theta, alpha, num_iters);figure;plot(1:numel(J_history), J_history, '-b', 'LineWidth', 2);xlabel('Number of iterations');ylabel('Cost J');